class Solution {
    public int numRabbits(int[] answers) {
        Map<Integer, Integer> map = new HashMap<>();
        for(int p : answers){
            map.put(p, map.getOrDefault(p, 0)+1);
        }
        int count = 0;
        for(int p : map.keySet()){
            int grp_size = p + 1;
            int reported_rabbits = map.get(p);
            int groups= (int)Math.ceil(reported_rabbits*1.0/ grp_size*1.0);
            count += groups * grp_size;
        }
        return count;
    }
}
