 * @return {number}
 */
var numRabbits = function(answers) {
    let countMap = new Map(); // Using a map to store the count of rabbits for each answer
    let count = 0;

    for (let i = 0; i < answers.length; i++) {
        let answer = answers[i];
        if (answer === 0) {
            count++; // Count rabbits that answered 0
        } else {
            if (!countMap.has(answer) || countMap.get(answer) === 0) {
                countMap.set(answer, answer); // Set the count for this answer
                count += answer + 1;
            } else {
                countMap.set(answer, countMap.get(answer) - 1);
            }
        }
    }

    return count;
};
