class Solution {
    public int numRabbits(int[] answers) {
        Map<Integer, Integer> map = new HashMap<>();
        for (int n : answers) map.put(n, map.getOrDefault(n, 0) + 1);
        int sum = 0;
        for (int key : map.keySet()) {
            int freq = map.get(key);
            if (freq - 1 == key) {
                sum += freq;
            } else if (freq <= key){
                sum += key + 1;
            } else {
                int t = freq / (key + 1);
                sum += t * (key + 1);
                if (freq % (key + 1) > 0) sum += key + 1;
            }
        }
        return sum;
    }
}
