class Solution {
    public int numRabbits(int[] answers) {
        HashMap<Integer, Integer> map = new HashMap<>();
        int sum = 0;
        for(int n: answers) {
            if(!map.containsKey(n)) {
                map.put(n,1);
            } else {
                map.put(n, map.get(n)+1);
            }
        }
        for(int key: map.keySet()) {
            int val = map.get(key);
            sum += (int) Math.ceil(val*1.0 / (key+1)) * (key+1);
        }
        return sum;
    }

}

