class Solution {
    public int surfaceArea(int[][] grid) {
        return (computeSide(grid)
             + computeSide(transpose(grid))
             + computeVertical(grid))*2;
    }

    private int[][] transpose(int[][] grid){
        int[][] result = new int[grid.length][grid.length];
        for (int r=0; r<grid.length; r++){
            for (int c=0; c<grid.length; c++){
                result[r][c]=grid[c][r];
            }
        }
        return result;
    }

    private int computeSide(int[][] grid){
        int total =0;

        for (int r[] : grid){
            int prev=0;
            for (int c : r){
                if (c > prev)
                    total += c - prev;

                prev=c;
            }
        }

        return total;
    }

    private int computeVertical(int[][] grid){
        int total =0;

        for (int r[] : grid){
            for (int c : r){
                if (c != 0)
                    total++;
            }
        }

        return total;
    }
}
