class Solution:
    def surfaceArea(self, a: List[List[int]]) -> int:
        answer = 0

        for i in range(len(a)):
            for j in range(len(a)):
                answer += self.helper(i, j , a)

        return answer

    def helper(self, i, j, a):
        area = 0
        count = a[i][j]

        if count == 0:
            return 0
        else:
            area += 2

        if i == 0:
            area += count

        if i == len(a) - 1:
            area += count

        if j == 0:
            area += count

        if j == len(a) - 1:
