        if i + 1 < len(a) and count > a[i+1][j]:
            area += count - a[i+1][j]

        if i-1 >= 0 and count > a[i-1][j]:
            area += count - a[i-1][j]

        if j -1 >= 0 and count > a[i][j-1]:
            area += count - a[i][j-1]

