public class Solution {
    public int SurfaceArea(int[][] grid) {
        var res = 0;
        for(var i =0; i<grid.Length; i++){
            for(var j=0; j<grid[i].Length; j++){
				//at top row 
                if(i==0){
                    res += grid[i][j];
                }
				//at bottom row 
                if(i==grid.Length-1){
                    res += grid[i][j];
                }
				//at left most column
                if(j==0){
                    res +=grid[i][j];
                }
				//at right most column
                if(j==grid[i].Length-1){
                    res+=grid[i][j];
                }
				//current position is higher than previous row
                if(i>0 && grid[i][j]>grid[i-1][j]){
                    res += grid[i][j]-grid[i-1][j];
                }
				//current position is higher than next row
                if(i<grid.Length-1 && grid[i][j]>grid[i+1][j]){
                    res += grid[i][j] -grid[i+1][j];
                }
				//current position is higher than previous column
                if(j>0 && grid[i][j]>grid[i][j-1]){
                    res += grid[i][j]-grid[i][j-1];
