class Solution {
public:
    int surfaceArea(vector<vector<int>>& grid) {
        int n = grid.size();
        int m = grid[0].size();
        int res = 0;
        int dr[] = {-1, 1, 0, 0};
        int dc[] = {0, 0, -1, 1};
        for(int i = 0;i<n;i++){
            for(int j= 0;j<m;j++){
                if(grid[i][j])  res += 2;
                int rr, cc;
                for(int k = 0;k<4;k++){
                    rr = dr[k] + i;
                    cc = dc[k] + j;
                    if(rr < 0 || cc < 0 || rr > n - 1 || cc > m - 1){
                        res += grid[i][j];
                    }
                    else{
                        res += max(grid[i][j] - grid[rr][cc], 0);
                    }
                }
            }
        }
        return res;
    }
};
