class Solution {
public:
    int surfaceArea(vector<vector<int>>& grid) {
        int cube = 0, sf = 0;
        for(int i = 0; i < grid.size(); ++i) {
            for(int j = 0; j < grid[0].size(); ++j) {
                if(grid[i][j]==0) {
                    continue;
                }
                cube += grid[i][j];
                sf += grid[i][j] - 1;
                if(j + 1 < grid[0].size()) {
                    sf += min(grid[i][j], grid[i][j + 1]);
                }
                if(i + 1 < grid.size()) {
                    sf += min(grid[i][j], grid[i + 1][j]);
                }
            }
        }
        return cube * 6 - sf * 2;
    }
};
