// loop 1 formula 
// 6 * num of stacked cubes - the top/bottom surface areas that "disappear" - the side surface areas that "disappear"
// 6 * grid[i][j] - 2 * min(grid[i][j], grid[i][j + 1])  - 2 * (grid[i][j] - 1)   

// loop 2 formula (need to account for the other "disappeared" sides between columns in the grid)
// 2 * the side surface areas that "disappear" between 2 rows (the trailing row and the current row)
// 2 * min(grid[i-1][j], grid[i][j])

    int surfaceArea(vector<vector<int>>& grid) {
       int sum = 0;

        for (int i = 0; i < grid.size(); ++i)
        {
            for(int j = 0; j < grid[i].size(); ++j)
            {
                sum += 6 * grid[i][j] - 2 * min(grid[i][j], j + 1 >= grid[i].size() ? 0 : grid[i][j+1]) - max(0, 2 * (grid[i][j] - 1));
            }
        }

        for(int i = 1; i < grid.size(); ++i)
        {
            for(int j = 0; j < grid[i].size(); ++j)
            {
                sum -= 2 * min(grid[i - 1][j], grid[i][j]);
            }
        }

        return sum;
    }

