class Solution {
    public int surfaceArea(int[][] grid) {
        int area = 0;
        for (int i = 0; i < grid.length; i++) {
            for (int j = 0; j < grid[0].length; j++) {
                int h = grid[i][j];
                if(h>=1) {
                    area += (h - 1) * 4 + 6;
                    if(i-1>=0 && grid[i-1][j]!=0) area -= Math.min(h, grid[i-1][j]);
                    if(i+1<grid.length && grid[i+1][j]!=0) area -= Math.min(h, grid[i+1][j]);
                    if(j-1>=0 && grid[i][j-1]!=0) area -= Math.min(h, grid[i][j-1]);
                    if(j+1<grid[0].length && grid[i][j+1]!=0) area -= Math.min(h, grid[i][j+1]);
                }
            }
        }
        return area;
    }
}

