const surfaceArea = grid => {
    let total = 0, n = grid.length;

    for (let i = 0; i < n; i++) {
        for (let j = 0; j < n; j++) {
            let v = grid[i][j];
            let area = v === 0 ? 0 : 2;

            if (i === 0) { area += v; }
            else { area += Math.max(0, v - grid[i - 1][j]); }

            if (j === 0) { area += v; }
            else { area += Math.max(0, v - grid[i][j - 1]); }

            if (i + 1 === n) { area += v; }
            else { area += Math.max(0, v - grid[i + 1][j]); }

            if (j + 1 === n) { area += v; }
            else { area += Math.max(0, v - grid[i][j + 1]); }

            total += area;
        }
    }

    return total;
};
