class Solution {
    public int surfaceArea(int[][] grid) {
        int row = grid.length, col = grid[0].length ;
        int top = 0, l=0;
        for(int i = 0; i < row ; ++i){
           for(int j = 0; j < col ; ++j) {
               l += i==0 ? grid[i][j] : (int)  Math.abs(grid[i][j] - grid[i-1][j]);
               if(i == row-1)l += grid[i][j];
               l += j==0 ? grid[i][j] : (int)  Math.abs(grid[i][j] - grid[i][j-1]);
               if(j == col-1)l += grid[i][j];
               top += grid[i][j] > 0 ? 1 : 0;
           }
        }
        return 2*(top)+l;
    }
}
