int surfaceArea(vector<vector<int>>& grid) {

    int ans=0;
    int m=grid.size();
    int n=grid[0].size();

    if(m==0 || n==0)
        return 0;

    if(m==1 && n==1)
        return grid[0][0]*4+2;

    int dx[]={-1,0,1,0};
    int dy[]={0,1,0,-1};

    for(int i=0;i<m;i++)
        for(int j=0;j<n;j++)
        {
            if(grid[i][j]==0)
                continue;

            if((i==0 && j==n-1) || (i==m-1 && j==0) || (i==0 && j==0) || (i==m-1 && j==n-1))
                ans+=2*grid[i][j];
            else
            if(i==0 || j==0 || i==m-1 || j==n-1)
                ans+=grid[i][j];

            for(int k=0;k<4;k++)
                { 
                  int nx=i+dx[k];
                  int ny=j+dy[k];
                  if(nx>=0 && ny>=0 && nx<m && ny<n)
                      ans+=(grid[nx][ny]-grid[i][j]>=0 ? 0 : grid[i][j]-grid[nx][ny]);
                }
                ans+=2;
        }
    return ans;
}

