class Solution {
    public int surfaceArea(int[][] grid) {
        int result = 0, n = grid.length;
        for (int i = 0; i < n; i++) {
            for (int j = 0; j < n; j++) {
                if (grid[i][j] == 0) {
                    continue;
                }
                int preI = i == 0 ? 0 : grid[i - 1][j];
                int preJ = j == 0 ? 0 : grid[i][j - 1];
                int nextI = i == n - 1 ? 0 : grid[i + 1][j];
                int nextJ = j == n - 1 ? 0 : grid[i][j + 1];
                result += 2 + Math.max(0, grid[i][j] - preI) + Math.max(0, grid[i][j] - preJ) +
                    Math.max(0, grid[i][j] - nextI) + Math.max(0, grid[i][j] - nextJ);
            }
        }
        return result;
    }
}
