            areaLeft = (j - 1 < 0) ? 0 : CUBE_AREA(grid[i][j - 1]);
            cubeLeft = (j - 1 < 0) ? 0 : grid[i][j - 1];
            areaRight = (j + 1 < gridColSize[i]) ? CUBE_AREA(grid[i][j + 1]) : 0;
            cubeRight = (j + 1 < gridColSize[i]) ? grid[i][j + 1] : 0;
            areaUp = (i - 1 < 0) ? 0 : CUBE_AREA(grid[i - 1][j]);
            cubeUp = (i - 1 < 0) ? 0 : grid[i - 1][j];
            areaDown = (i + 1 < gridSize) ? CUBE_AREA(grid[i + 1][j]) : 0;
            cubeDown = (i + 1 < gridSize) ? grid[i + 1][j] : 0;

            currentArea = CUBE_AREA(grid[i][j]);
