class Solution {
    public int surfaceArea(int[][] grid) {
        int area = 0;
        int n = grid.length;
        if(n==1){
            return 4*grid[0][0]+2;
        }
        for(int i=0;i<n;i++){
            for(int j=0;j<n;j++){
                //top and bottom
                if(grid[i][j] != 0){
                    area+=2;
                }
                //edges
                if(i == 0 || j==0 || i == n-1 || j==n-1){
                    area += grid[i][j];
                }
                //corners
                if(i==0&&j==0){
                    area += grid[i][j];
                }
                if(i==0&&j==n-1){
                    area += grid[i][j];
                }
                if(i==n-1&&j==0){
                    area += grid[i][j];
                }
                if(i==n-1&&j==n-1){
                    area += grid[i][j];
                }
                // rest of them
                if(i != n-1 && j != n-1){
                    area += Math.abs(grid[i][j]-grid[i+1][j]);
                    area += Math.abs(grid[i][j]-grid[i][j+1]);
                }else if(i != n-1){
                    area += Math.abs(grid[i][j]-grid[i+1][j]);
                }else if(j != n-1){
                    area += Math.abs(grid[i][j]-grid[i][j+1]);
                }
            }
        }
        return area;
    }
}
