class Solution {
public:
    int surfaceArea(vector<vector<int>>& grid) {
        int n=grid.size();
        int m=grid[0].size();
        if(n==1 and m==1)
        return 4*grid[0][0]+2;
        if((n==1 and m==2) or (n==2 and m==1))
        {
            if(n==1 and m==2)
            return 4+3*(grid[0][0]+grid[0][1])+abs(grid[0][0]-grid[0][1]);
            if(n==2 and m==1)
            return 4+3*(grid[0][0]+grid[1][0])+abs(grid[0][0]-grid[1][0]);
        }
        int ans=0;
        for(int i=0;i<n;i++)
        {
            for(int j=0;j<m;j++)
            {
                int l=0,b=0;
                if(j!=0)
                l=abs(grid[i][j]-grid[i][j-1]);
                if(i!=n-1)
                b=abs(grid[i][j]-grid[i+1][j]);
                int h=grid[i][j];
                int hc=0;
                if((i==0 and (j==0 || j==m-1)) || (i==n-1 and (j==0 || j==m-1)))
                hc=2;
                else if(i==0 || j==0 || i==n-1 || j==m-1)
                hc=1;
                ans+=l+b+hc*h;
                if(h)
                ans+=2;
            }
        }
        return ans;
    }
};
