impl Solution {
    pub fn surface_area(grid: Vec<Vec<i32>>) -> i32 {
        let dir = [0, 1, 0, usize::MAX, 0];
        let mut ans = 0;
        for i in 0..grid.len() {
            for j in 0..grid[i].len() {
                if grid[i][j] == 0 {
                    continue;
                }
                ans += 2;
                for d in 0..4 {
                    let i2 = i.wrapping_add(dir[d]);
                    let j2 = j.wrapping_add(dir[d + 1]);
                    if i2 >= grid.len()
                    || j2 >= grid[i].len() {
                        ans += grid[i][j];
                    }
                    else if grid[i][j] > grid[i2][j2] {
                        ans += grid[i][j] - grid[i2][j2];
                    }
                }
            }
        }
        ans
    }
}
