defmodule Solution do
  @spec surface_area(grid :: [[integer]]) :: integer
  def surface_area(grid) do
    2 * hori(grid) +
      verti(grid) + 
      (Enum.zip_with(grid, &(&1)) |> verti())
  end

  defp hori(grid) do
    Enum.map(grid, fn row ->
      Enum.count(row, &(&1 != 0))
    end)
    |> Enum.sum()
  end

  defp verti(grid) do
    Enum.map(grid, fn row ->
      Enum.map_reduce(row, 0, fn x, acc ->
        {abs(x - acc), x}
      end)
      |> then(fn {list, acc} ->
        Enum.sum(list) + acc
      end)
    end)
    |> Enum.sum()
  end
end
