class Solution {
 int surfaceArea(List<List<int>> grid) {
    int n = grid.length;
    int area = 0;

    for (int i = 0; i < n; i++) {
        for (int j = 0; j < n; j++) {
            if (grid[i][j] > 0) {
                area += (grid[i][j] * 4) + 2; // Contribution from top and bottom faces

                // Check adjacent cells and subtract common faces
                if (i > 0) {
                    area -= min(grid[i][j], grid[i - 1][j]) * 2;
                }
                if (j > 0) {
                    area -=min(grid[i][j], grid[i][j - 1]) * 2;
                }
            }
        }
    }

    return area;
}

}
