class Solution:
    def surfaceArea(self, grid: List[List[int]]) -> int:

        sumX, sumY, n, m = 0, 0, len(grid), len(grid[0])      

        sumZ = sum(1 for i, j in product(range(n), range(m)) if grid[i][j] > 0)

        for row in grid:
            sumX += row[0]+row[-1]
            for i in range(1, m):
                sumX += abs(row[i]-row[i-1])    

        for column in zip(*grid):
            sumY += column[0]+column[-1]
            for i in range(1, n):
                sumY += abs(column[i]-column[i-1])

        return sumX+sumY+sumZ*2

