public class Solution {
    public int SurfaceArea(int[][] grid) {
        int count = 0;
        for(int i = 0; i < grid.Length; i++)
          for(int j = 0; j < grid[0].Length; j++)
            count += Calc(i,j,grid);
        return count;
    }
    private static int Calc(int i, int j, int[][] grid)
    {
      int n = grid[i][j];
      if(n == 0) return 0;
      int count = 0;
      count = n * 4 + 2;
      if(i != 0)
        count -= Math.Min(n, grid[i-1][j]);
      if(i != grid.Length - 1)
        count -= Math.Min(n, grid[i+1][j]);
      if(j != 0)
        count -= Math.Min(n, grid[i][j - 1]);
      if(j != grid[0].Length - 1)
        count -= Math.Min(n, grid[i][j + 1]);
      return count;
    }
}
