class Solution {
    fun surfaceArea(grid: Array<IntArray>): Int {
        var totalArea = 0
        val h = grid.size
        val w = grid[0].size

        for (i in 0 until h) {
            for (j in 0 until w) {

                // top and bottom surface area
                if (grid[i][j] > 0) totalArea += 2

                // calculate surface area by checking the surrounding cube
                // checking top
                if (i-1 >= 0) {
                    if (grid[i][j] > grid[i-1][j]) totalArea += grid[i][j] - grid[i-1][j]
                } else {
                    totalArea += grid[i][j]
                }

                // checking right
                if (j+1 < w) {
                    if (grid[i][j] >  grid[i][j+1]) totalArea += grid[i][j] - grid[i][j+1]
                } else {
                    totalArea += grid[i][j]
                }

                // checking bottom
                if (i+1 < h) {
                    if (grid[i][j] > grid[i+1][j]) totalArea += grid[i][j] - grid[i+1][j]
                } else {
                    totalArea += grid[i][j]
                }

                // checking left
                if (j-1 >= 0) {
                    if (grid[i][j] > grid[i][j-1]) totalArea += grid[i][j] - grid[i][j-1]
                } else {
                    totalArea += grid[i][j]
                }

            }
        }

        return totalArea
    }
}
