class Solution {
    public int surfaceArea(int[][] grid) {
        int n = grid.length;
        int surfaceArea = 0;

        for (int i = 0; i < n; i++) {
            for (int j = 0; j < n; j++) {
                int height = grid[i][j];

                if (height > 0) {
                    // Обчислюємо площу основи куба
                    surfaceArea += 2;

                    // Додаємо площу бокової поверхні куба
                    surfaceArea += 4 * height;

                    // Віднімаємо площу, яку закривають сусідні куби
                    if (i > 0) {
                        surfaceArea -= Math.min(height, grid[i - 1][j]);
                    }
                    if (i < n - 1) {
                        surfaceArea -= Math.min(height, grid[i + 1][j]);
                    }
                    if (j > 0) {
                        surfaceArea -= Math.min(height, grid[i][j - 1]);
                    }
                    if (j < n - 1) {
                        surfaceArea -= Math.min(height, grid[i][j + 1]);
                    }
                }
            }
        }

        return surfaceArea;
    }
}

