class Solution {
public:
    int R, C, ans=0;
    int area(int row , int col , vector<vector<int>>& grid){
        int cur=0 , up, down, left, right;

        if(!grid[row][col]) return cur;

        cur+=2;

        if(row==0)      up=grid[row][col];
        else            up = grid[row][col]-grid[row-1][col];
        if(row==R-1)    down=grid[row][col];
        else            down = grid[row][col]-grid[row+1][col];

        if(col==0)      left=grid[row][col];
        else            left = grid[row][col]-grid[row][col-1];
        if(col==C-1)    right=grid[row][col];
        else            right = grid[row][col]-grid[row][col+1];

        if(up>0)    cur+=up;
        if(down>0)  cur+=down;
        if(left>0)  cur+=left;
        if(right>0) cur+=right;

        return cur;
    }
    int surfaceArea(vector<vector<int>>& grid) {

        R=grid.size();
        C=grid[0].size();

        for(int i =0 ;i< R;i++){
            for(int  j =0 ;j< C ;j++){
                ans+=area(i,j,grid);
            }
        }
        return ans;
    }
};
