class Solution {
    int calcSurfaceAreaOfOneTower(int height) {
        if (height == 0) return 0;
        return 2 + (4*height);
    }
public:
    int surfaceArea(vector<vector<int>>& grid) {
        int total = 0;
        int n = grid.size();
        for (int i = 0; i < n; i++) {
            vector<int> row = grid.at(i);
            for (int j = 0; j < n; j++) {
                int height = row.at(j);
                int local = calcSurfaceAreaOfOneTower(height);

                // subtract (shared side w/ right) * 2
                if (j+1 < row.size()) {
                    int right = row.at(j+1);
                    local -= 2*min(height, right);
                }

                // subtract (shared side w/ down) * 2
                if (i+1 < n) {
                    int down = grid.at(i+1).at(j);
                    local -= 2*min(height, down);
                }

                total += local;
            }
        }
        return total;
    }
};
```

