class Solution:
    def surfaceArea(self, grid: List[List[int]]) -> int:
        totalArea = 0
        nRows = len( grid )
        nCols = len( grid[ 0 ] )
        # For each tower of cubes
        for iRow in range( nRows ):
            for iCol in range( nCols ):
                v = grid[ iRow ][ iCol ]
                # Always add one for the top face and one for the bottom except if tower is 0 high
                towerArea = 2 if v > 0 else 0                
                # Calculate surface area on North face:
                if iRow > 0:
                    vNorth = grid[ iRow - 1][ iCol ]
                    if v > vNorth:
                        towerArea += v - vNorth
                else:
                    towerArea += v
                # On South face:
                if iRow < nRows - 1:
                    vSouth = grid[ iRow + 1][ iCol ]
                    if v > vSouth:
                        towerArea += v - vSouth
                else:
                    towerArea += v
                # On West face:
                if iCol > 0:
                    vWest = grid[ iRow ][ iCol - 1 ]
                    if v > vWest:
                        towerArea += v - vWest
                else:
                    towerArea += v
                # On East face
                if iCol < nCols - 1:
                    vEast = grid[ iRow ][ iCol + 1 ]
                    if v > vEast:
                        towerArea += v - vEast
                else:
                    towerArea += v
                totalArea += towerArea

        return totalArea

