class Solution {
    public int surfaceArea(int[][] grid) {
        int totalSurfaceArea = 0;
        int len = grid.length;
        for (int i = 0; i<len; i++){
            for (int j = 0; j<len; j++){
                if (grid[i][j] > 0){
                    totalSurfaceArea += 6 * grid[i][j] - 2*(grid[i][j] - 1);
                } 
                if (i>0){
                    totalSurfaceArea -= 2 * Math.min(grid[i-1][j], grid[i][j]);
                }
                if (j>0){
                    totalSurfaceArea -= 2 * Math.min(grid[i][j-1], grid[i][j]);
                }
            }
        }
        return totalSurfaceArea;
    }
}
