class Solution {
public:
    int surfaceArea(vector<vector<int>>& grid) {
        const auto m = grid.size(), n = grid.begin()->size();
        auto surface = 0;
        for (auto i = 0; i < m; ++i) {
            for (auto j = 0; j < n; ++j) {
                if (grid[i][j]) {
                    surface += 2;
                    if (i == 0) surface += grid[i][j];
