class Solution {
public:

    int getAdjacents(int i , int j , int n , int m , vector<vector<int>> &grid){
        int ct = 0;
        // left
        if(j==0){
            ct += grid[i][j];
        }
        else{
            ct += (grid[i][j]>grid[i][j-1] ? grid[i][j]-grid[i][j-1] :0);
        }

        // right
        if(j==m-1){
            ct += grid[i][j];
        }
        else{
            ct += (grid[i][j]>grid[i][j+1] ? grid[i][j]-grid[i][j+1] :0);
        }

        // top
        if(i==0){
            ct += grid[i][j];
        }
        else{
            ct += (grid[i][j]>grid[i-1][j] ? grid[i][j]-grid[i-1][j] :0);
        }

        // bottom
        if(i==n-1){
            ct += grid[i][j];
        }
        else{
            ct += (grid[i][j]>grid[i+1][j] ? grid[i][j]-grid[i+1][j] :0);
        }


        return ct;
    }

    int surfaceArea(vector<vector<int>>& grid) {
        int n  = grid.size();
        int m = grid[0].size();

        int ct = 0;

        for(int i=0 ; i<n ; i++){
            for(int j=0 ; j<m ; j++){
                ct += getAdjacents(i , j , n , m , grid);
                if(grid[i][j]!=0) ct += 2;
            }

        }


        return ct;
    }
};
