impl Solution {
    pub fn surface_area(grid: Vec<Vec<i32>>) -> i32 {
        let n = grid.len();
        let mut area = 0;

        for i in 0..n {
            for j in 0..n {
                area += 2 + grid[i][j] * 4;

                if grid[i][j] == 0 {
                    area -= 2 + grid[i][j] * 4;
                }

                // check up
                if i >= 1 {
                    if grid[i][j] <= grid[i - 1][j] {
                        area -= grid[i][j];
                    } else {
                        area -= grid[i - 1][j];
                    }
                }
                // check down
                if i + 1 < n {
                    if grid[i][j] <= grid[i + 1][j] {
                        area -= grid[i][j];
                    } else {
                        area -= grid[i + 1][j];
