class Solution {
    fun surfaceArea(grid: Array<IntArray>): Int {
        var c = 2 * grid.size * grid.size
        var zeroCount = 0

        var sum = 0

        for (i in 0 until grid.size) {
            for (j in 0 until grid[0].size) {

                if (grid[i][j] == 0) {
                    zeroCount++
                    continue
                }

                val top = grid.getOrNull(i - 1)?.get(j) ?: 0
                val right = grid[i].getOrNull(j + 1) ?: 0
                val bottom = grid.getOrNull(i + 1)?.get(j) ?: 0
                val left = grid[i].getOrNull(j - 1) ?: 0

                val v = grid[i][j]

                if (v > right) {
                    sum += (v - right)
                }
                if (v > top) {
                    sum += (v - top)
                }
                if (v > bottom) {
                    sum += (v - bottom)
                }
                if (v > left) {
                    sum += (v - left)
                }
            }
        }

        return sum + c - 2 * zeroCount
    }
}
```

