class Solution {
    public int surfaceArea(int[][] grid) {
        int res = 0;

        for(int y = 0; y < grid.length; y++){
            for(int x = 0; x < grid[0].length; x++){
                if(grid[y][x] > 0) res += grid[y][x] * 4 + 2; // regard it is a single tower
                if(x > 0) res -= Math.min(grid[y][x-1], grid[y][x]) * 2; // substract the double counted area
                if(y > 0) res -= Math.min(grid[y-1][x], grid[y][x]) * 2; // substract the double counted area
            }
        }

        return res;
    }
}
