function surfaceArea(grid: number[][]): number {
    const n = grid.length;

    const visited: number[][] = [];
    for (let i = 0; i < n; i++) {
        visited.push(new Array(n).fill(0));
    }

    let total = 0;
    for (let i = 0; i < n; i++) {
        for (let j = 0; j < n; j++) {
            const v = grid[i][j];

            if (v && !visited[i][j]) {
                total += calculateSquare(grid, i, j, visited);
            }
        }
    }

    return total;
}

function calculateSquare(
    grid: number[][], 
    si: number, 
    sj: number, 
    visited: number[][]
): number {
    const stack = [[si, sj]];
    let total = 0;

    while (stack.length) {
        const [i, j] = stack.pop();

        if (visited[i][j]) {
            continue;
        }

        visited[i][j] = 1;

        const cells = [
            [i, j - 1],
            [i, j + 1],
            [i - 1, j],
            [i + 1, j],
        ];

        const v = grid[i][j];
        total += 2;

        cells.forEach(([ci, cj]) => {
            const cv = grid[ci] && grid[ci][cj] || 0;
            const s = v - cv;
            total += 0 < s ? s : 0;

            if (cv) {
                stack.push([ci, cj]);
            }
        });
    }

    return total;
}
