class Solution {
    public boolean isMonotonic(int[] nums) {

        for(int a = 0; a < nums.length-1; a++){
            int diff1 = nums[a+1]-nums[a];// First value that representing difference

            while(diff1 == 0){// Control for difference without 0
                a++;
                if(a+1==nums.length){
                    return true;
                }
                diff1 = nums[a+1]-nums[a];
            }
            if(diff1>0){// Control for other differences
                for(int b = a+1; b < nums.length-1; b++){// If difference is different we will return false;
                    int diff2 =  nums[b+1]-nums[b];
                    if(diff2<0){
                        return false;
                    }
                }
                return true;
            }else if(diff1<0){
                for(int b = a+1; b < nums.length-1; b++){
                    int diff2 =  nums[b+1]-nums[b];
                    if(diff2>0){
                        return false;
                    }
                }
                return true;
            }
        }
        return true;
    }
}
