var isMonotonic = function(nums) {
    let status = CONST.EQUAL;

    for (let i = 0; i < nums.length - 1; i++) {
        if (nums[i] < nums[i + 1] && [CONST.EQUAL,CONST.INCREASING].includes(status)) {
            status = CONST.INCREASING;
        } else if (nums[i] > nums[i + 1] && [CONST.EQUAL, CONST.DECREASING].includes(status)) {
            status = CONST.DECREASING;
        } else if (nums[i] !== nums[i + 1]) {
            return false;
        }
    }

    return true;
};
