var isMonotonic = function(A) {
    let increasing = true, decreasing = true;
    for (i = 0; i < A.length; i++) {
        if (A[i] < A[i+1]) decreasing = false;
        else if (A[i] > A[i+1]) increasing = false;
        else if (A[i] === A[i+1]) continue;
    }
    return increasing || decreasing;
};
