    else if(root->val>r) {
        if(root->left) dfs(root->left,l,r,res);
    }
    else if(root->val<l)if(root->right) dfs(root->right,l,r,res);

}
int rangeSumBST(struct TreeNode* root, int L, int R){
    int res = 0;
    dfs(root,L,R,&res);
    return res;
}
