class Solution {
public:

    // recursive solution

	``
    //one simple trick you bcan use is that you can find the inorder of bst and add l to r all values
	``

    int rangeSumBST(TreeNode* root, int L, int R) {
         int ans = 0;
        dfs(root, L, R,ans);
        return ans;
    }

    void dfs(TreeNode* node, int L, int R,int & ans) {
        if (node != NULL) {
            if (L <= node -> val && node ->  val <= R)
                ans += node -> val;
            if (L < node -> val)
                dfs(node -> left, L, R,ans);
            if (node ->  val < R)
                dfs(node -> right, L, R,ans);
        }
    }
};
