use std::rc::Rc;
use std::cell::RefCell;
impl Solution {
    pub fn range_sum_bst(root: Option<Rc<RefCell<TreeNode>>>, low: i32, high: i32) -> i32 {
        let mut stack: std::collections::VecDeque<Option<Rc<RefCell<TreeNode>>>> = std::collections::VecDeque::new();
        stack.push_back(root);

        let mut sum:i32 = 0;
        while let Some(stack_object) = stack.pop_front() {
            let node = stack_object.as_ref().unwrap().replace(TreeNode::new(0));
            if node.val <= high && node.val >= low { 
                sum += node.val; 
            }

            if node.val > low && node.left.is_some() { stack.push_back(node.left); }
            if node.val < high && node.right.is_some() { stack.push_back(node.right); }
        }
        sum
    }
}
```

