class Solution {
    public int rangeSumBST(TreeNode root, int low, int high) {
        if(root == null ) return 0;
        if(root.left ==null && root.right == null) return root.val;
        return travers(root, low, high, 0);
    }
    int travers(TreeNode root, int l, int h, int sum ){
        if(root == null ) return 0;
        int some = 0;
        if(root.val >= l && root.val <= h){
            some = root.val;
        }
        int lsum = travers(root.left, l,h,sum);
        int rsum = travers(root. Right, l ,h, sum);
        return lsum + rsum+ some;
    }
}

class Solution {
    //Taking a global int to store the sum
    int sum = 0;
    //traverse the tree and use the property of the binary search tree, i.e, inorder traveral of tree would give you the sorted data
    public int rangeSumBST(TreeNode root, int low, int high) {
        if(root==null)
            return 0;
        if(root.val>=low && root.val<=high){
            sum+=root.val;
            rangeSumBST(root.left,low,high);
            rangeSumBST(root.right,low,high);
        }  
        if(root.val<low)
            rangeSumBST(root.right,low,high); //only right because root val is low so left is always smaller and we can neglect it
        else if(root.val>high)
            rangeSumBST(root.left,low,high); //only left because root val is low so right is always higher and we can neglect it
        return sum;
    }
}

