class Solution {
    final int MAX = (int)1e5;
    boolean[] vis;
    int[] occ;
    int[] LPF; // Least prime factor

    int dfs(int x) {
        if (x > MAX || vis[x] == true) return 0;
        vis[x] = true;

        int cnt = occ[x];

        if (x == LPF[x]) { // if prime
            for (int i = x+x; i <= MAX; i += x) {
                cnt += dfs(i);
            }
        }
        else if (occ[x] > 0) { // if cotains in nums array
            for (x = x; x > 1; x /= LPF[x]) { // Log(x) prime factorization 
                cnt += dfs(LPF[x]);
            }
        }

        return cnt;
    }

    public int largestComponentSize(int[] nums) {
        occ = new int[MAX+10];
        for (int num : nums) occ[num]++;

        LPF = new int[MAX+10];
        for (int i = 2; i <= MAX; i++) {
            if (LPF[i] == 0) {
                for (int j = i; j <= MAX; j += i) {
                    if (LPF[j] == 0) LPF[j] = i;
                }
            }
        }

        vis = new boolean[MAX+10];
        int ans = 0;
        for (int i = 2; i <= MAX; i++) {
            ans = Math.max(ans, dfs(i));
        }

        return ans;
    }
}
