```
class Solution {
public:

    // M = MAX ELEMENT
    // TIME COMPLEXITY:- O(MLOG(M))
    // SPQACE COMPLEXITY:- O(M)

     // finds the parent of a connected component
    int find_set(int x,vector<pair<int,int>>& parent){
        return parent[x].first==x?x:parent[x].first = find_set(parent[x].first,parent);
    }

    // unites two connected component if they have different parent's
    void union_set(int x,int y,vector<pair<int,int>>& parent,vector<int>& comp){
        int xroot = find_set(x,parent),yroot = find_set(y,parent);
        if(xroot==yroot)
            return; // have same parent

        // rank compression 
        if(parent[xroot].second==parent[yroot].second)
            parent[xroot].second++;
        if(parent[xroot].second>parent[yroot].second){
            parent[yroot].first = xroot;
            comp[xroot] += comp[yroot]; // increase the component size on merging
        }
        else{
            parent[xroot].first = yroot;
            comp[yroot] += comp[xroot]; // increase the component size on merging
        }
    }

    int largestComponentSize(vector<int>& nums) {
        int N = *max_element(nums.begin(),nums.end());
        vector<bool> isPresent(N+1,false);

        for(auto& c:nums)
            isPresent[c] = true;

        // use NlogN time to mark all numbers which have same factor i
        vector<int> adj[N+1];
        for(int i=2;i<=N;i++){
            for(int j=i;j<=N;j+=i){
                if(isPresent[j])
                    adj[i].push_back(j);
            }
        }

        // parent[i].first = store parent of the connected component
        // parent[i].second = store rank of the connected component
        // comp[i] will store size of connected component

        vector<int> comp(N+1);
        vector<pair<int,int>> parent;
        for(int i=0;i<=N;i++){
            if(isPresent[i])
                comp[i] = 1;
            parent.push_back({i,0});
        }

        // unite two numbers which has same factor i
        for(int i=1;i<=N;i++){
            for(int j=1;j<adj[i].size();j++)
                union_set(adj[i][j-1],adj[i][j],parent,comp);
        }

        // find the maximum size of connected component
        int ans = 0;
        for(auto& c:comp)
            ans = max(ans,c);

        return ans;
    }
};
```

