    parent[q] = p;
  }

  const m = Math.max(...A);
  let p = new Array(m+1).fill(true);
  const root = Math.floor(Math.sqrt(m));
  for(let i=2;i<=root;i++) if(p[i]) {
    let k = -1;
    for(let j=0;j<n;++j) {
      if(A[j]%i===0) {
        while(A[j] % i === 0)
          A[j] = Math.floor(A[j]/i);
        if(k===-1) k = j;
        else union(k, j);
      }
    } 
    for(let j=i*i;j<=m;j+=i) p[j] = false;
  }

  A = A.map((x,i)=>[x,i]).filter(x=>x[0]>1).sort((a,b)=>a[0]-b[0]);
  for(let i=1;i<A.length;++i) {
    if(A[i-1][0]===A[i][0]) {
      union(A[i-1][1], A[i][1]);
    }
  }

  return Math.max(...cnt);
};
```

solutions.content:
For each number `x` in the input array `A`, use an associative array `m` to map factors of `x` to `x`.  There are 3 use cases to consider for each factor of `x`:

* **Case 1:** `x` as a factor of itself
* **Case 2:** the i<sup>th</sup> factor of `x`
* **Case 3:** the j<sup>th</sup> factor of `x`, where `j` = `x / i`

Since we are using union-find to track the parent representatives `P` of each disjoint set, we only need to track a single member `x` of each set for each factor `x`, `i`, `j` for case 1, 2, 3 correspondingly.  As sets are joined, their length `L` is accumulated and tracked by each parent reprentative.  Return the maxmium length of any parent reprensentative's set.

---

*Kotlin*
```
class Solution {
    fun largestComponentSize(A: IntArray, N: Int = (1e5 + 1).toInt()): Int {
        var P = IntArray(N) { it }              // 🙂 parent representative of disjoint sets
        var L = IntArray(N) { 1 }               // 🤥 length of parent representative's set
        fun find(x: Int): Int {
            if (P[x] == x)
                return x
            P[x] = find(P[x])
            return P[x]
        }
        fun union(a_: Int, b_: Int) {
            var (a, b) = listOf(a_, b_)
            a = find(a)
            b = find(b)
            if (a == b)
                return
            P[a] = b; L[b] += L[a]  // arbitrary choice
        }
        var m = mutableMapOf<Int, Int>()  // x,i,j factors of x -> x
        for (x in A) {
            if (m.contains(x)) union(m[x]!!, x) else m[x] = x      // case 1: x as a factor of itself
            for (i in 2..Math.floor(Math.sqrt(x.toDouble())).toInt()) {
                if (x % i != 0)
                    continue
                var j = x / i
                if (m.contains(i)) union(m[i]!!, x) else m[i] = x  // case 2: i-th factor of x
                if (m.contains(j)) union(m[j]!!, x) else m[j] = x  // case 3: j-th factor of x
            }
        }
        return L.max()!!  // 🎯 maximum length of any parent representative's set
    }
}
```

