            a = find(a);
            b = find(b);
            if (a == b)
                return;
            P[b] = a;  // arbitrary choice
            L[a] += L[b];
        };
        for (auto x: A) {
            if (m[x]) _union(m[x], x); else m[x] = x;      // case 1: x as a factor of itself
            for (auto i{ 2 }; i * i <= x; ++i) {
                if (x % i)
