                if (m[i]) _union(m[i], x); else m[i] = x;  // case 2: i-th factor of x
                if (m[j]) _union(m[j], x); else m[j] = x;  // case 3: j-th factor of x
            }
        }
        return *max_element(L.begin(), L.end());           // 🎯 maximum length of any parent representative's set
    }
};
