class UnionFind {
    vector<int> parent, size;
public:
    UnionFind(int n) {
        parent.resize(n); size.resize(n);
        for (int i = 0; i < n; i++) {
            parent[i] = i, size[i] = 1;
        }
    }

    int Find(int x) {
        if (x == parent[x]) return x;
        return parent[x] = Find(parent[x]); // Path compression
    }

    bool Union(int u, int v) {
        int pu = Find(u), pv = Find(v);
        if (pu == pv) return false; // Return False if u and v are already part of the same component
        if (size[pu] > size[pv]) { // Union by larger size
            size[pu] += size[pv];
            parent[pv] = pu;
        } else {
            size[pv] += size[pu];
            parent[pu] = pv;
        }
        return true;
    }

    int getComponentSize(int x) {
        return size[Find(x)];
    }
};

class Solution {
public:
    vector<int> spf; // spf[x] is the smallest prime factor of number x, where x >= 2

    int largestComponentSize(vector<int>& nums) {
        int maxNum = *max_element(nums.begin(), nums.end());
        UnionFind uf(maxNum + 1);
        sieve(maxNum);

        for (int x : nums) {
            for (int pf : getPrimeFactors(x)) {
                uf.Union(pf, x);
            }
        }

        int res = 1; // max_component_size
        map<int, int> component_size;
        for (int x : nums) {
            int parent = uf.Find(x);
            component_size[parent]++;
            res = max(res, component_size[parent]);
        }

        return res;
    }

    void sieve(int n) {
        spf.resize(n+1);
        for (int i = 2; i <= n; ++i)
            spf[i] = i;

        for (int i = 2; i * i <= n; i++) {
            if (spf[i] != i) continue; // skip if `i` is not a prime number
            for (int j = 2*i; j <= n; j += i) {
                if (spf[j] > i) { 
                    spf[j] = i;
                }
            }
        }
    }

    vector<int> getPrimeFactors(int n) { // O(logN)
        vector<int> factors;
        while (n > 1) {
            factors.push_back(spf[n]);
            int s = spf[n]; // smallest prime factor
            while (n % s == 0) { n /= s;}
        }
        return factors;
    }

};
