class Solution {
public:
    int arr[(int)(1e5+5)];
    int visit[(int)(1e5+5)]={0};
    int member[(int)(1e5+5)];
    constexpr void build(vector<int>& primes, pair<int,int>* sp, int N){
        if(primes.empty()){
            visit[0] = visit[1] = 1;
            sp[1].first = sp[1].second = 1;
            for(int i=2;i<=1e5;i++){
                if(!visit[i]){
                    primes.push_back(i);
                    sp[i].first=i;
                    sp[i].second = i;
                }for (int j=0; i*primes[j]<=1e5; j++){
                     visit[i*primes[j]] = 1;
                     sp[i*primes[j]].first = primes[j];
                     sp[i*primes[j]].second = sp[i].first == primes[j] ? sp[i].second : i;
                     if(i%primes[j]==0)break;
                 }
            }
        }
        for(int i=0;i<=N;i++){
            arr[i] = i;
        }
    }
    int find(int x){
        while(arr[x]!=x)
            x=arr[x];
        return x;
    }
    void uni(int x, int y){
        x = find(x), y = find(y);
        if(x==y)return;
        member[y]+=member[x];
        member[x]=0;
        arr[x] = y;
    }
    int largestComponentSize(vector<int>& nums) {
        static vector<int> primes;
        static pair<int,int> sp[(int)(1e5+5)];
        build(primes, sp, *max_element(nums.begin(),nums.end()));
        int ans=1;
        for(auto& it:nums){
            int pre=sp[it].first;
            it = sp[it].second;
            while(it!=sp[it].first){
                uni(sp[it].first, pre);
                pre = sp[it].first;
                it = sp[it].second;
            }
            uni(sp[it].first, pre);
            member[find(pre)]++;
            ans = max(member[find(pre)], ans);
        }
        return ans;
    }
};
